$(document).ready((function(){
	$("[data-bs-hover-animate]").mouseenter((function(){
		var a=$(this);
		a.addClass("animated "+a.attr("data-bs-hover-animate"))
}
	)).mouseleave((function(){
		var a=$(this);
		a.removeClass("animated "+a.attr("data-bs-hover-animate"))
}
	)),$("[data-bs-tooltip]").tooltip()
}
));
document.getElementById("sltdcolor").addEventListener("change", colorchange);
function colorchange(){
	var x = document.getElementById("sltdcolor").value;
	document.body.style.backgroundColor=x;

}
